function doProductViewByAT(mboxName, params, selector) {
  if (adobe && adobe.target) {
    if (document.querySelectorAll && document.querySelectorAll(selector).length > 0) {
      adobe.target.getOffer({
       "mbox": mboxName,
       "params": params !== undefined ? params : {},
       "success": function(offers) {
         if (offers.length > 0) {
           adobe.target.applyOffer({
             "mbox": mboxName,
             "timeout": 150000,
             "selector": selector,
             "offer": offers
           });
         } else {
           jQuery(selector).css({"visibility": "visible"});
         }
       },
       "error": function(status, error) {
         if (console && console.log) {
           _satellite.logger.info("SATELLITE[CUSTOM]: Error Occured! : mbox '" + mboxName + "' " + status + ": " + error, 1);
           _satellite.logger.info("SATELLITE[CUSTOM]: show default contents: mbox '" + mboxName + "'", 1);
            jQuery(selector + " .at-content").css({"visibility": "visible"});
           console.log('Error', status, error);
         }
       },
       "timeout": 1500,
     });
    } else {
       _satellite.logger.info("SATELLITE[CUSTOM]: show default contents: mbox[X] '" + mboxName + "'", 1);
       jQuery(selector + " .at-content").css({"visibility": "visible"});
    }
  } else {
     _satellite.logger.info("SATELLITE[CUSTOM]: show default contents: mbox[X] '" + mboxName + "'", 1);
     jQuery(selector + " .at-content").css({"visibility": "visible"});
  }
}

function sendConversionEvent(params) {
  if (adobe && adobe.target && params.productName !== undefined && params.productName !== null && params.productName !== '') {
    adobe.target.trackEvent({
      "mbox": "orderConfirmPage",
      "params": {
        "orderId": getPurchaseId(),
        "orderTotal": 0,
        "productPurchasedId": params.productId
      }
    });
  }
  function getPurchaseId() {
    return "GWC" + new Date().getTime().toString(16) + Math.floor(10000000000*Math.random()).toString();
  }
}

function sendEventMboxErrorOrNoResult(mboxName, params, selector) {
  $(document).trigger('at-embedded-noresult.gwc', {
    "mbox": mboxName,
    "params": params !== undefined ? params : {},
    "selector": selector
  });
}

function doGetAndApplyOfferByAT(mboxName, params, selector) {
  if (!params.recsItemCount) {
    params.recsItemCount = 6;
  }
  params.selector = selector;
  if (adobe && adobe.target) {
    if (document.querySelectorAll && document.querySelectorAll(selector + " .at-offer").length > 0) {
      adobe.target.getOffer({
       "mbox": mboxName,
       "params": params !== undefined ? params : {},
       "success": function(offers) {
         if (offers.length > 0) {
           _satellite.logger.info("SATELLITE[CUSTOM]:  '" + mboxName + "'", 1);
           offers[0].content = "<script type=\"text/javascript\">var mboxSelector = '" + selector + "';</" + "script>" + "<script type=\"text/javascript\">var recsItemCount = '" + params.recsItemCount + "';</" + "script>" + offers[0].content;
           adobe.target.applyOffer({
             "mbox": mboxName,
             "timeout": 3000,
             "selector": selector + " .at-offer",
             "offer": offers
           });
         } else {
           _satellite.logger.info("SATELLITE[CUSTOM]: show default contents: mbox[X] '" + mboxName + "'", 1);
           jQuery(selector + " .at-content").css({"visibility": "visible"});
           sendEventMboxErrorOrNoResult(mboxName, params, selector);
         }
       },
       "error": function(status, error) {
         if (console && console.log) {
           _satellite.logger.info("SATELLITE[CUSTOM]: Error Occured! : mbox '" + mboxName + "' " + status + ": " + error, 1);
           _satellite.logger.info("SATELLITE[CUSTOM]: show default contents: mbox '" + mboxName + "'", 1);
            jQuery(selector + " .at-content").css({"visibility": "visible"});
            sendEventMboxErrorOrNoResult(mboxName, params, selector);
           console.log('Error', status, error);
         }
       },
       "timeout": 3000,
     });
    } else {
       _satellite.logger.info("SATELLITE[CUSTOM]: show default contents: mbox[X] '" + mboxName + "'", 1);
       jQuery(selector + " .at-content").css({"visibility": "visible"});
       sendEventMboxErrorOrNoResult(mboxName, params, selector);
    }
  } else {
     _satellite.logger.info("SATELLITE[CUSTOM]: show default contents: mbox[X] '" + mboxName + "'", 1);
     jQuery(selector + " .at-content").css({"visibility": "visible"});
     sendEventMboxErrorOrNoResult(mboxName, params, selector);
  }
}

try {
  document.addEventListener(adobe.target.event.REQUEST_SUCCEEDED, function(e) {
    window.ttMETA = window.ttMETA || {};
    console.log(e.detail);
    if (e.detail.mbox !== "") {
      window.ttMETA[e.detail.mbox] = {};
      var tokens = e.detail.responseTokens;
      if (isEmpty(tokens)) {
        return;
      }
      for (var key in tokens[tokens.length-1]) {
        window.ttMETA[e.detail.mbox][key] = tokens[tokens.length-1][key];
      }
      // Call External Function
      $(document).trigger('launch-rt-handle.gwc', {
        mbox: e.detail.mbox,
        ttMETA: ttMETA[e.detail.mbox]
      });
    }
    console.log(window.ttMETA);
    function isEmpty(val){
      return (val === undefined || val == null || val.length <= 0) ? true : false;
    }
  });
} catch (e) {

