}
.holdings-footer__pagetop__in {
  display: flex;
  justify-content: flex-end;
  margin: 0 auto;
  max-width: 1280px;
}
.holdings-footer__pagetop a {
  display: flex;
  align-items: center;
  justify-content: center;
  background-color: #E6E6E6;
  border-radius: 4px;
}
.holdings-footer__pagetop a img {
  display: block;
}

@media (hover: hover) {
